SELECT  (mean("immissione") / mean("produzione") ) * 100 as "percimmissione"   FROM
( 
SELECT last("value") - 67.02 as "prelievo" FROM "autogen"."data" WHERE ("device" = 'SDM1.1' AND "type" = 'Import')
), 
(
SELECT last("value")  as "immissione" FROM "autogen"."data" WHERE ("device" = 'SDM1.1' AND "type" = 'Export') 
),
(
SELECT last("value") -20.22 as "produzione" FROM "autogen"."data" WHERE ("device" = 'SDM1.2' AND "type" = 'Import') 
)

SELECT   (mean("produzione") - mean("immissione")) / (mean("prelievo") + mean("produzione")  - mean("immissione")) * 100 as "percautoconsumo"    FROM
( 
SELECT last("value") - 67.02 as "prelievo" FROM "autogen"."data" WHERE ("device" = 'SDM1.1' AND "type" = 'Import')
), 
(
SELECT last("value")  as "immissione" FROM "autogen"."data" WHERE ("device" = 'SDM1.1' AND "type" = 'Export') 
),
(
SELECT last("value") -20.22 as "produzione" FROM "autogen"."data" WHERE ("device" = 'SDM1.2' AND "type" = 'Import') 
)

